package cse;

public class Comparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a = "Java";
        String b = "java";
        System.out.println(a.equals(b));             
        System.out.println(a.equalsIgnoreCase(b));   
        System.out.println(a.compareTo(b)); 
	}

}
